U' (mOptions, preload) {	mOptions, textMenuProc,	NoItems,				/* disable everything, program does the enabling */	enabled, "Options",	 {		"Line Drawing",			noicon, nokey, nomark, plain;		"Flat Shading",			noicon, nokey, nomark, plain;		"Flat Shading with Outline",			noicon, nokey, nomark, plain;		"-",			noicon, nokey, nomark, plain;		"Draw In B/W",			noicon, nokey, nomark, plain;		"Draw In Color",			noicon, nokey, nomark, plain;		"-",			noicon, nokey, nomark, plain;		"Backfaced Polygon Removal",			noicon, nokey, nomark, plain;		"Double Buffer",			noicon, nokey, nomark, plain;	}};/* this ALRT and DITL are used as an About screen */resource 'ALRT' (rAboutAlert, purgeable) {	{40, 20, 160, 297},	rAboutAlert,	{ /* array: 4 elements */		/* [1] */		OK, visible, silent,		/* [2] */		OK, visible, silent,		/* [3] */		OK, visible, silent,		/* [4] */		OK, visible, silent	}};resource 'DITL' (rAboutAlert, purgeable) {	{ /* array DITLarray: 5 elements */		/* [1] */		{88, 185, 108, 265},		Button {			enabled,			"OK"		},		/* [2] */		{8, 8, 24, 214},		StaticText {			disabled,			"Virtual Sphere Sample v1.1"		},		/* [3] */		{32, 8, 48, 296},		StaticText {			disabled,			"Copyright й Apple Computer Inc. 1993."		},		/* [4] */		{56, 8, 72, 136},		StaticText {			disabled,			"Brought to you by:"		},		/* [5] */		{80, 24, 112, 167},		StaticText {			disabled,			"Michael Chen, Human Interface Group / ATG"		}	}};/* this ALRT and DITL are used as an error screen */resource 'ALRT' (rUserAlert, purgeable) {	{40, 20, 120, 260},	rUserAlert,	{ /* array: 4 elements */		/* [1] */		OK, visible, silent,		/* [2] */		OK, visible, silent,		/* [3] */		OK, visible, silent,		/* [4] */		OK, visible, silent	}};resource 'ALRT' (130, purgeable) {	{40, 20, 154, 260},	130,	{	/* array: 4 elements */		/* [1] */		OK, visible, silent,		/* [2] */		OK, visible, silent,		/* [3] */		OK, visible, silent,		/* [4] */		OK, visible, silent	}};resource 'DITL' (rUserAlert, purgeable) {	{ /* array DITLarray: 3 elements */		/* [1] */		{50, 150, 70, 230},		Button {			enabled,			"OK"		},		/* [2] */		{10, 60, 30, 230},		StaticText {			disabled,			"Sample - Error occurred!"		},		/* [3] */		{8, 8, 40, 40},		Icon {			disabled,			2		}	}};resource 'DITL' (rMessageAlert, purgeable) {	{	/* array DITLarray: 3 elements */		/* [1] */		{87, 150, 107, 230},		Button {			enabled,			"OK"		},		/* [2] */		{10, 60, 72, 230},		StaticText {			disabled,			"^0"		},		/* [3] */		{8, 8, 40, 40},		Icon {			disabled,			2		}	}};resource 'WIND' (rWindow, preload, purgeable) {	{60, 60, 60+300, 60+300},	noGrowDocProc, visible, noGoAway, 0x0, "Virtual Sphere"};/* here is the quintessential MultiFinder friendliness device, the SIZE resource */resource 'SIZE' (-1) {	dontSaveScreen,	acceptSuspendResumeEvents,	enableOptionSwitch,	canBackground,				/* we can background; we don't currently, but our sleep value */								/* guarantees we don't hog the Mac while we are in the background */	multiFinderAware,			/* this says we do our own activate/deactivate; don't fake us out */	backgroundAndForeground,	/* this is definitely not a background-only application! */	dontGetFrontClicks,			/* change this is if you want "do first click" behavior like the Finder */	ignoreChildDiedEvents,		/* essentially, I'm not a debugger (sub-launching) */	is32BitCompatible,		/* this app should not be run in 32-bit address space */	reserved,	reserved,	reserved,	reserved,	reserved,	reserved,	reserved,	kPrefSize * 1024,	kMinSize * 1024	};resource 'PAT#' (rDitherPatterns, "Dither Patterns") {	{	/* array PatArray: 65 elements */		/* [1] */		$"FFFF FFFF FFFF FFFF",		/* [2] */		$"FEFF FFFF FFFF FFFF",		/* [3] */		$"FEFF FFFF EFFF FFFF",		/* [4] */		$"FEFF FFFF EEFF FFFF",		/* [5] */		$"EEFF FFFF EEFF FFFF",		/* [6] */		$"EEFF FBFF EEFF FFFF",		/* [7] */		$"EEFF FBFF EE