ze			actualSize;	AEDesc			dataDesc = {typeNull, NULL},					propertyDesc = {typeNull, NULL};	// Use to pass no properties	OSErr			err;	if (typeMyText != textDesc->descriptorType)		return(errAETypeError);			GetRawDataFromDescriptor(textDesc, (Ptr)&aTextToken, sizeof(aTextToken), &actualSize);		err = GetTextTextProperty(&aTextToken, &dataDesc);	if (noErr != err) goto done;		err = CreateText(cText, &dataDesc, insertHereDesc, &propertyDesc, result);done:	if (dataDesc.dataHandle)		AEDisposeDesc(&dataDesc);	return(err);}OSErr	CloneDesc(AEDesc* aDesc, AEDesc* insertHereDesc, AEDesc* result){	AEDesc		cloneDesc = {typeNull, NULL},				textDesc = {typeNull, NULL};	OSErr		err;		if (typeObjectSpecifier == aDesc->descriptorType)		err = AEResolve(aDesc, kAEIDoMinimum, &cloneDesc);	else		err = AEDuplicateDesc(aDesc, &cloneDesc);			if (noErr != err) goto done;		switch (cloneDesc.descriptorType)	{		case typeAEList:			err = errAETypeError;			// We can't handle copying more than one item to the scrap			break;					default:			err = AECoerceDesc(&cloneDesc, typeMyText, &textDesc);			if (noErr != err) goto done;			err = CloneTextDesc(&textDesc, insertHereDesc, result);	}	done:	if (cloneDesc.dataHandle)		AEDisposeDesc(&cloneDesc);	if (textDesc.dataHandle)		AEDisposeDesc(&textDesc);		return(err);}