#pragma segment Mainvoid DuplicateStyleTERec( TEHandle  hSourceTE,													TEHandle *hDestTE,													Rect     *destRect,													GrafPtr   destPort ){	GrafPtr oldPort;	short   oldSelStart;	short   oldSelEnd;	StScrpHandle printerTextStyles;	// Set up the ports	GetPort(&oldPort);	SetPort(destPort);	// Create a temporary Text Edit and copy the windows text edit into it	*hDestTE = TEStyleNew(destRect, destRect);// Select all the text (preserving the previous settings) so that we can use // GetTylScrap (or TEGetStyleScrapHandle ) to get the style of the whole TERec	oldSelStart = (*hSourceTE)->selStart;	oldSelEnd   = (*hSourceTE)->selEnd;	TESetSelect(0,(*hSourceTE)->teLength, hSourceTE);// Get the style	printerTextStyles = TEGetStyleScrapHandle(hSourceTE);// Revert the selection range	TESetSelect(oldSelStart, oldSelEnd, hSourceTE);// Move the text from the documents TERec and add the style (got above) to it	HLock((Handle)((*hSourceTE)->hText));	TEStyleInsert ( (Ptr)*((*hSourceTE)->hText),									(*hSourceTE)->teLength,									printerTextStyles,									*hDestTE);			HUnlock((Handle)((*hSourceTE)->hText));// Deactivat the temporary TERec	TEDeactivate(*hDestTE);	// Reset the port	SetPort(oldPort);}