the time when the most temporary memory	is in use.  We need the segments in use at that time */resource 'seg!' (256, purgeable) {	{		"GNonRes";		"GClipboard";		"GClose";		"GFile";		"GOpen";		"GReadFile";		"GSelCommand";		"GFinder";		"GWriteFile";		"BBNonRes";		"BBOpen";		"GNonRes2";		"GPrint";		"GReadResource";		"TESelCommand";	}};#if qPowerPC#include "CodeFragmentTypes.r"resource 'cfrg' (0, "Configuration Info") {	{		kPowerPC,		kFullLib,		kNoVersionNum,		kNoVersionNum,		0,		0,		kIsApp,		kOnDiskFlat,		kZeroOffset,		kWholeFork,		"AEGestalt"	}};#endif// APPLEEVENTS resource 'aedt' (kGestaltAEEvents) {		// AE Dispatch table resource	{	kMacAppClass, kAEGetConfig, cAEProvideGestaltInfo;	}};//	AE Class, AE Message ID, command Number// VERS// Version for MouseInfoRESOURCE 'vers' (1,#if qNames	"Application Version",#endif	purgeable) {	0x01,	0x00,	final,	0x01,	verUs,	"1.0",	"1.0, Application created by MacApp® and Mountain Dew®"};// ALERTS AND DLOGS include "Defaults.rsrc" 'ALRT'	(phAboutApp);		// About… windowinclude "Defaults.rsrc" 'DITL'	(phAboutApp);		// About… contentsinclude "Defaults.rsrc" 'STR#'	(kDefaultCredits);	// credits// ICONs and BUNDLE// Get the default MacApp® application icon and file referenceinclude "Defaults.rsrc"  'ICN#' (128);// CMNUs include "Defaults.rsrc" 'CMNU' (mApple);			// grab the default Apple/File menusresource 'CMNU' (mFile,#if qNames"mFile",#endifnonpurgeable) {	mFile,	textMenuProc,	EnablingManagedByMacApp,	enabled,	"File",	{	"New",				noIcon, "N",	noMark, plain, cNew;	"Close",			noIcon, "W",	noMark, plain, cClose;	"-",				noIcon, noKey,	noMark, plain, nocommand;	"Quit",				noIcon, "Q",	noMark, plain, cQuit	}};resource 'CMNU' (mExamine,#if qNames"mExamine",#endifnonpurgeable) {	mExamine,	textMenuProc,	allEnabled,	enabled,	"Examine",	{	"Find…",			noIcon, "F",	noMark, plain, cFindAEGestalt;	"Report…",			noIcon, "R",	noMark, plain, cCreateReport	}};// MBARs // Displayed menus resource 'MBAR' (kMBarDisplayed,#if qNames"Windoid",#endif	purgeable) {	{mApple; mFile; mExamine}};//--------------------------------------------------------------------------------------------------// 	Icons, Bundles and FRefs… Oh my! (don't forget the Signature)//	It is very important that the Signature is registered here, because//	we are using it as a way to register the application PPC port, and //	otherwise we will get a default name, which will not show up in the//	PPCBrowse filtering function. I learned this hard lesson 3.23 am//	and it was even a Saturday morning, sigh!type kSignature as 'STR ';resource kSignature (0,#if qNames"Signature",#endif	purgeable) {	"AEGestalt 3.0b2 ©Apple Computer, Inc. 1991"};resource 'FREF' (kApplicationID,#if qNames"AEGestalt Application",#endif	purgeable) {	'APPL',	0,	""};resource 'FREF' (kDocumentID,#if qNames"AEGestalt Document",#endif	purgeable) {	kFileType,	1,	""};resource 'FREF' (kStationeryID,#if qNames"AEGestalt Stationery",#endif	purgeable) {	kStationery,	2,	""};resource 'BNDL' (kBundleID,#if qNames"AEGestalt",#endif	purgeable) {	kSignature,	0,	{ /* array TypeArray: 2 elements */		/* [1] */		'ICN#',		{ /* array IDArray: 3 elements */			0, kApplicationID,			1, kDocumentID,			2, kStationeryID		},		/* [2] */		'FREF',		{ /* array IDArray: 3 elements */			0, kApplicationID,			1, kDocumentID,			2, kStationeryID		}	}};// About Dialog resources + Views #################################################################################################################// a couple of the resources are actually unused, but I left them in because I might need them// in another project :-)include "PICT.rsrc";					// PICT resource (1002)resource 'STR#' (10006, "Window titles", purgeable) {	{	/* array StringArray: 1 elements */		/* [1] */		"About MouseInfo"	}};resource 'STR#' (100