	virtual void	DoPaste();	virtual void	DoClear();	virtual unsigned long CalcIdle();	virtual Boolean	HaveSelection();	virtual void	DoSelectAll();	// new public methods	void			AdjustScrollValues(Boolean mustRedraw);	TEClickLoopUPP  GetClickLoop();	TEHandle		GetTEHandle();	void			GetVisTERgn(RgnHandle rgn);};#endif