	gMovieBackGrnd = false;		// movie in foreground	gDone = false;				// set global loop var to false while program active	gMovieOpen = false;			// No window open}// Gestalt tests#pragma segment Mainvoid TestforQuickTimeVersion(void){	long	QTimeVersion;	OSErr	error;	if(Gestalt('qtim',&QTimeVersion) == noErr)	{		 	/* QuickTime version 2.1 or later required for all options to be available.		 	   If not display a warning dialog and disable QT 2.1 reliant feature */		 if (QTimeVersion < 0x02100000)		{			DisplayAlert (rGenWarning,rQTmessages,2);			DisableItem(GetMenuHandle(mMode),iModifier); 		} // check for the QuickTime shared library loaded as Gestalt not always correct #if defined(powerc) || defined(__powerc)		if (!EnterMovies)		{			DisplayAlert(rGenAlert,rQTmessages,3);			gDone = true;			return;		}#endif		error = EnterMovies();	// initialise QuickTime		if (error != noErr)		{			DisplayAlert (rGenAlert,rQTmessages,1);			gDone = true;		// exit application		}		return;	}	DisplayAlert (rGenAlert,rQTmessages,1);	gDone = true;	return;}// Main function#pragma segment Mainint	main(void){	long 	*AppSize;						// application size 	short	i;		AppSize = (long*)(GetApplLimit());	SetApplLimit (AppSize-32768);	// This decreases the application heap by 32k, which in turn	// increases the stack by 32k.	MaxApplZone();	// Expand the heap so code segments load at the top.	for (i=0;i<5;i++)		MoreMasters();				// allocate more master pointers	//      Initialise the toolbox	InitGraf (&qd.thePort);	InitFonts();	InitWindows();	InitMenus();	TEInit();	InitDialogs(0L);	InitCursor();		InitGlobals();					// initialise globals	MenuBarInit();					// init menubar	TestforQuickTimeVersion();		// test for and initialise Quicktime		DoAdjustMenus(); 				// menu setup	EventLoop();					// Call the main event loop.	ExitToShell();					// Quit the application.	return 0;}