tionMainScreen};resource 'DITL' (768, purgeable) {	{	/* array DITLarray: 3 elements */		/* [1] */		{97, 198, 117, 270},		Button {			enabled,			"OK"		},		/* [2] */		{8, 8, 87, 271},		StaticText {			disabled,			"Abnormal termination (^0)"		},		/* [3] */		{0, 0, 0, 0},		HelpItem {			disabled,			HMScanhdlg {				1			}		}	}};resource 'CNTL' (kBlocksizePopup) {	{0, 0, 19, 180},	popupTitleLeftJust,	visible,	100,			// title width, pixels	kBlocksizePopup,	popupMenuCDEFproc + popupFixedWidth,	0,	"Blocksize: "};resource 'MENU' (kBlocksizePopup, preload) {	kBlocksizePopup, textMenuProc,	~0,	enabled, "Blocksize",	{		"1", noicon, nokey, nomark, plain;		"2", noicon, nokey, nomark, plain;		"4", noicon, nokey, nomark, plain;		"8", noicon, nokey, nomark, plain;		"16", noicon, nokey, nomark, plain;		"32", noicon, nokey, nomark, plain;		"64", noicon, nokey, nomark, plain;		"128", noicon, nokey, nomark, plain;		"256", noicon, nokey, nomark, plain;		"512", noicon, nokey, nomark, plain;		"1K", noicon, nokey, nomark, plain;		"2K", noicon, nokey, nomark, plain;		"4K", noicon, nokey, nomark, plain;		"8K", noicon, nokey, nomark, plain;		"16K", noicon, nokey, nomark, plain;		"32K", noicon, nokey, nomark, plain;		"64K", noicon, nokey, nomark, plain;		"128K", noicon, nokey, nomark, plain;		"256K", noicon, nokey, nomark, plain;		"512K", noicon, nokey, nomark, plain;		"1024K", noicon, nokey, nomark, plain;		"2048K", noicon, nokey, nomark, plain;	}};resource 'CNTL' (kAlignPopup) {	{0, 0, 19, 180},	popupTitleLeftJust,	visible,	100,			// title width, pixels	kAlignPopup,	popupMenuCDEFproc + popupFixedWidth,	0,	"Align: "};resource 'MENU' (kAlignPopup, preload) {	kAlignPopup, textMenuProc,	~0,	enabled, "Align",	{		"2", noicon, nokey, nomark, plain;		"4", noicon, nokey, nomark, plain;		"8", noicon, nokey, nomark, plain;		"16", noicon, nokey, nomark, plain;		"32", noicon, nokey, nomark, plain;		"64", noicon, nokey, no