 */	2,												/* minimum width */	0,	0,	0};resource 'thng' (128,  "Example Compressor",locked) {	compressorComponentType,	exampleCodecFormatType,	'appl',	codecInfoDoes32,	0,	'cdec',	128,	'STR ',	128,	'STR ',	129,	'ICON',	128};resource 'thng' (130,  "Example Decompressor",locked) {	decompressorComponentType,	exampleCodecFormatType,	'appl',	codecInfoDoes32,	0,	'cdec',	128,	'STR ',	130,	'STR ',	131,	'ICON',	130};resource 'ICON' (128, purgeable) {	$"0003 8000 0004 C000 0004 C000 0004 C000"	$"0004 C000 0004 C000 0004 C000 0004 C000"	$"0004 C000 0004 C000 0004 C000 0004 C000"	$"0004 C000 0004 C000 0004 C000 0004 C000"	$"0004 C000 0004 C000 0004 C000 0007 C000"	$"0008 6000 0018 7000 007F FC00 00C0 1E00"	$"0180 1F00 0100 0F00 0100 0F00 0300 0F80"	$"0200 0F80 03FF FF80 0C00 0FE0 0FFF FFE0"};resource 'STR ' (128) {	"My Example Compressor"};resource 'STR ' (129) {	"Compresses (in an exemplar fasion) an image into a YUVish format."};resource 'ICON' (130, purgeable) {	$"0200 0F80 03FF FF80 0C00 0FE0 0FFF FFE0"	$"0180 1F00 0100 0F00 0100 0F00 0300 0F80"	$"0008 6000 0018 7000 007F FC00 00C0 1E00"	$"0004 C000 0004 C000 0004 C000 0007 C000"	$"0004 C000 0004 C000 0004 C000 0004 C000"	$"0004 C000 0004 C000 0004 C000 0004 C000"	$"0004 C000 0004 C000 0004 C000 0004 C000"	$"0003 8000 0004 C000 0004 C000 0004 C000"};resource 'STR ' (130) {	"My Example Decompressor"};resource 'STR ' (131) {	"Decompresses an image conmpressed in YUVish format."};