rt					i;	GWorldPtr				srcGWorld;	PixMapHandle			srcPixMap;	PicHandle				pict;	Rect					pictBounds;	CGrafPtr				savedPort;	GDHandle				savedDevice;	ImageDescriptionHandle	desc;	Ptr						imageData;	long					size;	OSErr					err;		//	Load the picture and define the source and dest rects.	pict = GetPicture(128);	pictBounds = (**pict).picFrame;	OffsetRect(&pictBounds, -pictBounds.left, -pictBounds.top);	SizeWindow(gWindow, pictBounds.right, pictBounds.bottom, true);		//	Create a temporary offscreen used to store the pict.	if ( NewGWorld(&srcGWorld, 32, &pictBounds, nil, nil, 0) != noErr )		ExitToShell();	srcPixMap = GetGWorldPixMap(srcGWorld);		LockPixels(srcPixMap);		//	Draw the picture into the temporary gworld.		GetGWorld(&savedPort, &savedDevice);	SetGWorld(srcGWorld, nil);	DrawPicture(pict, &pictBounds);	SetGWorld(savedPort, savedDevice);		//	Now, compress the picture into a data buffer.	if ( GetMaxCompressionSize(srcPixMap, &(**srcPixMap).bounds, 32,						codecNormalQuality, kRawCodecType, bestSpeedCodec, &size) )		ExitToShell();	imageData = NewPtr(size);	desc = (ImageDescriptionHandle) NewHandle(sizeof(ImageDescription));	if ( err=CompressImage(srcPixMap, &(**srcPixMap).bounds, codecNormalQuality,					kRawCodecType, desc, imageData))		ExitToShell();		//	Decompress the image to the window.		SetWTitle(gWindow, "\pQuickTime ditherCopy");	for ( i=0; i<5; i++ ) {		EraseRect(&gWindow->portRect);		DecompressImage(imageData, desc, ((CGrafPtr)gWindow)->portPixMap, 					&pictBounds, &gWindow->portRect, ditherCopy | 0x80, nil);	}	while ( !Button() );		SetWTitle(gWindow, "\pQuickTime srcCopy");	for (i=0; i<5; i++) {		EraseRect(&gWindow->portRect);		DecompressImage(imageData, desc, ((CGrafPtr)gWindow)->portPixMap, 					&pictBounds, &gWindow->portRect, srcCopy, nil);	}	while ( !Button() );		SetWTitle(gWindow, "\pQuickDraw ditherCopy");	for ( i=0; i<5; i++ ) {		EraseRect(&gWindow->portRect);		CopyBits((BitMap *)*srcPixMap, &gWindow->portBits, &pictBounds,					&gWindow->portRect, ditherCopy, nil);	}	while ( !Button() );		SetWTitle(gWindow, "\pQuickDraw srcCopy");	for ( i=0; i<5; i++ ) {		EraseRect(&gWindow->portRect);		CopyBits((BitMap *)*srcPixMap, &gWindow->portBits, &pictBounds,					&gWindow->portRect, srcCopy, nil);	}	while ( !Button() );	DisposeGWorld( srcGWorld );	}