 *tTempFilePtr;/* Record for each fragment. Includes simplified version of the 'cfrg' data */struct internalItem{	/* Data used for the application */	Boolean			bDeleted;	Boolean			bExistsInDocument;	tTempFilePtr	tempFilePtr;		/* Data included in the 'cfrg' resource */	OSType 	archType;	long 	updateLevel;	long	currVersion;	long	oldDefVersion;	long	appStackSize;	short	appSubFolder;	short	usage;	short	location;	long	codeOffset;	long	codeLength;	Str255	name;};typedef struct internalItem tItem, *tItemPtr;struct internalResource {	long 			version;	long 			itemCount;	tItem			itemList[1];};typedef struct internalResource tHeader, *tHeaderPtr, **tHeaderHan;/* ===== Prototypes ===== */OSErr ParseResource (Handle theResource, tHeaderHan internalCopy);OSErr BuildResource (tHeaderHan internalCopy, Handle theResource);#endif // define __FRAGMENTS__