 kSleepTime;			InitListClickLoop ( );				// Creates the UPPs (etc) for the ClickLoop	InitDragHandlers ( );				// Creates the UPPs for the Drag Handlers	InstallAppleEventHandlers ( );		// Installs the AE handlers for the required AE		AdjustMenus ( );		// Create any other RoutineDescriptors we may need	gOutlineUserItemUPP = NewUserItemProc ( OutlineUserItem );		return;}//// We'll limit the scope of some of our function to the current source file// by  declairing them static. Although this has limited advantages as// far as encapsulation is concerned, it can make the source code a little// more readable.//static Boolean CheckConfiguration ( void ){	long		theResult;	OSErr		theErr;	Boolean		bHasAppleEvents, bHasFSpTraps, bHasFSpStdFile, bHasCodeFragmentManager;			// Verify that we can run on the current configuration		// We require AppleEvent Manager and FSSpec-based file traps and Standard File	theErr = Gestalt ( gestaltAppleEventsAttr, &theResult );	bHasAppleEvents = (theErr == noErr && (theResult & (1L << gestaltAppleEventsPresent)));		theErr = Gestalt ( gestaltFSAttr, &theResult );	bHasFSpTraps = (theErr == noErr && (theResult & (1L << gestaltHasFSSpecCalls)));		theErr = Gestalt ( gestaltStandardFileAttr, &theResult );	bHasFSpStdFile = (theErr == noErr && (theResult & (1L << gestaltStandardFile58)));		// Ensure CFM or CFM-68K is available	theErr = Gestalt ( gestaltCFMAttr, &theResult );	bHasCodeFragmentManager = (theErr == noErr && (theResult & (1L << gestaltCFMPresent)));	// We would also like the Drag Manager	theErr = Gestalt ( gestaltDragMgrAttr, &theResult );	gHasDragManager = (theErr == noErr && (theResult & (1L << gestaltDragMgrPresent)));	// It isn't enough to use Gestalt because we may not have sucessfully linked	// to the DragLib shared library. So, we also need to test one of the symbols	// against kUnresolvedSymbol to make sure we have a valid connection to it.	// Things like memory limitations to someone having the thing open with	// write permission could cause it to fail.	#if GENERATINGCFM	if ( gHasDragManager )		gHasDragManager = (InstallTrackingHandler != (void*) kUnresolvedCFragSymbolAddress);#endif		return (bHasAppleEvents & bHasFSpTraps & bHasFSpStdFile & bHasCodeFragmentManager);}