------------------------------------------------------------------ *//* UNIVERSAL DRIVER AND IO RELATED RESOURCES *//* ------------------------------------------------------------------------ */// Capturing PAP ImageWriters settings// how to capture this deviceresource gxCaptureType (gxCapturedAppleTalkType, sysheap, purgeable){	"\0D011ImageShared"};resource gxCaptureType (gxUncapturedAppleTalkType, sysheap, purgeable){	"\0D011ImageWriter"};resource gxCaptureType (gxCaptureStringID, sysheap, purgeable){	"\0X1B\0X62NAMELENPRINTERNAMETYPELENPRINTERTYPE\0X01*"};resource gxCaptureType (gxReleaseStringID, sysheap, purgeable){	"\0X1B\0X62NAMELENPRINTERNAMETYPELENPRINTERTYPE\0X01*"};// This resource customizes the behavior of the universal driver, allowing us// to use standard mechanisms for buffering and IO - all driver should have// one of these resources, although the universal driver does provide some default// values.resource gxUniversalIOPrefsType (gxUniversalIOPrefsID, sysheap, purgeable){	standardIO,	4,			// four buffers (univ driver WILL run with less if it needs to)	2500,		// we want them to each be large enough for one scan line of data (at least)	10,			// number of requests blocks to allocate for HIO	3*60*60,	// 3 minute timout for open/close	3*60*60,	// as well as for reading and writing};/* ------------------------------------------------------------------------ *//* RASTER GENERIC DRIVER RELATED RESOURCES *//* ------------------------------------------------------------------------ */// This resource customizes the behavior of the raster generic driver, allowing// us to specify our imaging options, as well as format/page scaling.// All raster drivers must have this resource.resource gxRasterPrefsType (gxRasterPrefsID, sysheap, purgeable){	gxDefaultRaster,	// default options are fine		0x00900000,	0x00900000,	// 144X144 dpi device	16,				// min band size == 2 head heights	0,				// max band size (0 is full page)	0x00008000,		// RAM percentage (50%)	100*1024,		// RAM slop (100K)	4,				// 4 bit device		{		gxDefaultOffscreen,									// YELLOW plane.  Use default halftoning.		0x000F0000,											// Angle = 15 degrees		0x002D0000,											// Freq = 45		gxRoundDot,											// RoundDot dithering		gxComponent3Tint,									// Extract yellow and dither it		gxRGBSpace, gxNoProfile, 0, 0, 0, 0,				// DotColor == black		gxRGBSpace, gxNoProfile, 0xFFFF, 0xFFFF, 0xFFFF, 0,	// Background color == white		gxCMYKSpace,										// Convert to gxCMYKSpace before halftoning.		gxNoSpace,											// No explicit color space		gxNoSet,											// No color set		gxNoProfile,										// No profile specified		gxDefaultOffscreen,									// MAGENTA plane.  Use default halftoning.		0x00000000,											// Angle = 0 degrees		0x002D0000,											// Freq = 45		gxRoundDot,											// RoundDot dithering		gxComponent2Tint,									// extract magenta and dither it		gxRGBSpace, gxNoProfile, 0, 0, 0, 0,				// dotColor == black		gxRGBSpace, gxNoProfile, 0xFFFF, 0xFFFF, 0xFFFF, 0,	// background color == white		gxCMYKSpace,										// Convert to gxCMYKSpace before halftoning.		gxNoSpace,											// No explicit color space		gxNoSet,											// No color set		gxNoProfile,										// No profile specified		gxDefaultOffscreen,									// CYAN plane.  Use default halftoning.		0x003C0000,											// Angle = 60 degrees		0x002D0000,											// Freq = 45		gxRoundDot,											// RoundDot dithering		gxComponent1Tint,									// Extract cyan and dither it		gxRGBSpace, gxNoProfile, 0, 0, 0, 0,				// DotColor == black		gxRGBSpace, gxNoProfile, 0xFFFF, 0xFFFF, 0xFFFF, 0,	// Background color == white		gxCMYKSpace,										// Convert to gxCMYKSpace before halftoning.		gxNoSpace,											// No explicit color space		gxNoSet,											// No color set		gxNoProfile,										// No profile specified		gxDefaultOffscreen,									// BLACK plane.  Use default halftoning.		0x002D0000,											// Angle = 45 degrees		0x002D0000,											// Freq = 45		gxRoundDot,											// RoundDot dithering		gxComponent4Tint,									// Extract black and dither it		gxRGBSpace, gxNoProfile, 0, 0, 0, 0,				// DotColor == black		gxRGBSpace, gxNoProfile, 0xFFFF, 0xFFFF, 0xFFFF, 0,	// Background color == white		gxCMYKSpace,										// Convert to gxCMYKSpace before halftoning.		gxNoSpace,											// No explicit color space		gxNoSet,											// No color set		gxNoProfile											// No profile specified		};};// This resource allows us to control the packaging that takes place within the// raster generic driver's RasterDataIn proc.  Drivers which do not use the Raster // generic RasterDataIn proc or override it entirely themselves need not have this resource.resource gxRasterPackType (gxRasterPackID, sysheap, purgeable){	2500,				// packing buffer size - for the ImageWriter 						// this is the # of bytes in our largest single packaged line		4,					// we are a CMYK (so we make 4 passes over the data)	16,					// we have a 16 pixel high head	2,					// it takes two passes to achive this 16 pixels	1,					// and there is a 1 pixel difference between these two passes	gxInterlaceColor,		// avoid ribbon contamination};// This resource allows us to control the remaining raster messages used for packaging.// This includes form feed, line feed, and package bitmap.// Drivers not using the Raster Generic Driver's implementations of these messages// need not have this resource.#define PERFSKIPOFF		"\0X1BD\0X00\0X04"#define PAPEROUTOFF		"\0X1Bo"#define UNIDIRECTIONAL	"\0X1B>"#define BIDIRECTIONAL	"\0X1B<"#define DPI144			"\0X1Bp"#define DPI160			"\0X1BP"#define DPI72			"\0X1Bn"#define DPI80			"\0X1BN"resource 'wstr' (gxPrintingDriverBaseID, sysheap, purgeable) {	PERFSKIPOFF PAPEROUTOFF UNIDIRECTIONAL DPI144};resource 'wstr' (gxPrintingDriverBaseID+1, sysheap, purgeable) {	PERFSKIPOFF PAPEROUTOFF UNIDIRECTIONAL DPI160};resource 'wstr' (gxPrintingDriverBaseID+2, sysheap, purgeable) {	PERFSKIPOFF PAPEROUTOFF BIDIRECTIONAL DPI72};resource 'wstr' (gxPrintingDriverBaseID+3, sysheap, purgeable) {	PERFSKIPOFF PAPEROUTOFF BIDIRECTIONAL DPI80};resource 'wstr' (gxPrintingDriverBaseID+10, sysheap, purgeable) {	"\0X0C",			// End page string:   a control-L for the IW's form feed};resource gxRasterPackOptionsType (gxRasterPackOptionsID, sysheap, purgeable){	gxPrintingDriverBaseID,	gxPrintingDriverBaseID + 10,		// forward line feed characteristics	98,					// max line feed amount is 98	gxRasterNumToASCII,	// express line feed as ASCII	2,					// minimum width is 2	"0",				// and pad with zeros	"\0X1BT",			// <esc>T == set line feed size	"\0X1Bf\0X0A",		// <esc>f<lf> == direction forward, do line feed		// reverse line feed characteristics	98,					// max line feed amount is 98	gxRasterNumToASCII,	// express line feed as ASCII	2,					// minimum width is 2	"0",				// and pad with zeros	"\0X1BT",			// <esc>T == set line feed size	"\0X1Br\0X0A",		// <esc>r<lf> == direction reverse, do line feed		// Here will eventually come data that controls the PackageBitmap message };/* ------------------------------------------------------------------------ *//* RESOURCES FOR STATUS AND DIALOGS *//* ------------------------------------------------------------------------ */resource 'stat'	(kDriverStatus, sysheap, purgeable) {	'Dipy',		{	// statusType			statusID	cool alert ID		statusString								code constant	printerReady,			1,	 		0,					"Sending data to printer";					// kSendingData	userAlert,				1,	 		kDriverStatus,		"Please check that the printer is on–line";	// kCheckOnline				// kSendingData	}};resource gxPrintingAlertType (kDriverStatus, sysheap, purgeable)			//	The printer isn't online{	printingStatus,																// version	cautionIcon,															// icon id	defaultSystemSize,	defaultAction,															// the default label	c