alse;					gSleepTime = kSleepTime;		InstallAppleEventHandlers ( );	CreateWindow ( );		// Create any other RoutineDescriptors we may need	gScrollControlActionUPP = NewControlActionProc ( ScrollControlActionProc );	gScrollThumbActionUPP = NewIndicatorActionProc ( ScrollThumbActionProc );		return;}static SInt16 CheckConfiguration ( void ){	SInt16		messageCode = 0;	SInt32		theResult;	OSErr		theErr;			// Verify that we can run on the current configuration		// We require AppleEvent Manager and color Quickdraw	theErr = Gestalt ( gestaltAppleEventsAttr, &theResult );	if ( !(theErr == noErr && (theResult & (1L << gestaltAppleEventsPresent)) ))		messageCode = kNeedSystem7;		theErr = Gestalt ( gestaltQuickdrawFeatures, &theResult );	if ( !(theErr == noErr && (theResult & (1L << gestaltHasColor)) ))		messageCode = kNeedColorQuickdraw;			return messageCode;}