tuff(void){	InitGraf(&qd.thePort);	InitFonts();	InitWindows();	InitMenus();	TEInit();	InitDialogs(nil);	InitCursor();	FlushEvents(everyEvent,0);}Boolean GetFileToOpen(FSSpec *target){	StandardFileReply sfReply;		StandardGetFile(nil,-1,nil,&sfReply);	BlockMove(&sfReply.sfFile,target,sizeof(FSSpec));		return sfReply.sfGood;}OSErr GetTargetAddress(Str255 prompt,Str255 appStr,PortInfoRec *myPortInfo,						AEAddressDesc *targetAddress,TargetID *toTargetID){	OSErr err;		err = PPCBrowser(prompt,appStr,false,&toTargetID->location,myPortInfo,nil,"\p");	if (err==noErr) {		toTargetID->name = myPortInfo->name;		err = AECreateDesc(typeTargetID,(Ptr)toTargetID,sizeof(TargetID),targetAddress);	}		return err;}OSErr SendODOC(AEAddressDesc *targetAddress,AliasHandle targetFile){	OSErr err;	AppleEvent theAE,reply;	AEDescList descList;		reply.dataHandle = nil;		err = AECreateAppleEvent(kCoreEventClass,kAEOpenDocuments,targetAddress,				kAutoGenerateReturnID,kAnyTransactionID,&theAE);	if (err!=noErr)		return err;		err = AECreateList(nil,0,false,&descList);	if (err!=noErr)		return err;		HLock((Handle)targetFile);	err = AEPutPtr(&descList,0,typeAlias,(Ptr)*targetFile,sizeof(AliasHandle)+(**targetFile).aliasSize);	HUnlock((Handle)targetFile);	if (err!=noErr)		return err;		err = AEPutParamDesc(&theAE,keyDirectObject,&descList);	if (err!=noErr)		return err;		err = AESend(&theAE,&reply,kAENoReply,kAENormalPriority,kAEDefaultTimeout,nil,nil);		err = AEDisposeDesc(&descList);	err = AEDisposeDesc(&theAE);	if (reply.dataHandle)		err = AEDisposeDesc(&reply);	return err;}