eek wp = (WindowPeek)FrontWindow();	register WindowPtr wp = FrontWindow();	//short kind = wp ? wp->windowKind : 0;	short kind = GetWindowKind(wp);		Boolean DA = kind < 0;	enable( fileMenu, quitItem, true );	enable( patMenu, pat1Item, gCurrentPat != pat1Item );	enable( patMenu, pat2Item, gCurrentPat != pat2Item );	enable( patMenu, pat3Item, gCurrentPat != pat3Item );	enable( patMenu, pat4Item, gCurrentPat != pat4Item );	enable( patMenu, pat5Item, gCurrentPat != pat5Item );	enable( patMenu, pat6Item, gCurrentPat != pat6Item );	enable( patMenu, pat7Item, gCurrentPat != pat7Item );	enable( patMenu, pat8Item, gCurrentPat != pat8Item );		enable( animMenu, startItem, gCurrentMove != START );	enable( animMenu, stopItem, gCurrentMove != STOP );	enable( animMenu, forItem, gCurrentDir != FORWARD );	enable( animMenu, revItem, gCurrentDir != REVERSE );	enable( animMenu, colorItem, gCurrentColor != COLOR );	enable( animMenu, grayItem, gCurrentColor != GRAY );	enable( animMenu, resetItem, true );}void enable( menu, item, ok )MenuHandle menu;{	if (ok)		//EnableItem( menu, item );		EnableMenuItem( menu, item );	else		//DisableItem( menu, item );		DisableMenuItem( menu, item );}void handleMenu( mSelect )long mSelect;{	int			menuID = HiWord( mSelect );	int			menuItem = LoWord( mSelect );	//GrafPtr		savePort;	//Str255		name;	//RGBColor	aColor;		switch (menuID)	{		case appleID:			if (menuItem == 1)			{				gCurrentPat = -1;				defineColorPalette();				updatePalette();				createImage();				drawImage();			}			else			{				/*GetPort( &savePort );				GetMenuItemText( appleMenu, menuItem, name );				OpenDeskAcc( (ConstStr255Param)name );				SetPort( savePort );*/			}			break;					case fileID:			switch (menuItem)			{				case quitItem:					cleanUp();					break;			}			break;					case patID:			gCurrentPat = menuItem;			createImage();			drawImage();			break;					case animID:			switch (menuItem)			{				case startItem:					gCurrentMove = START;					break;				case stopItem:					gCurrentMove = STOP;					break;				case forItem:					gCurrentDir = FORWARD;					break;				case revItem:					gCurrentDir = REVERSE;					break;				case colorItem:					defineColorPalette();					updatePalette();					break;				case grayItem:					defineGrayPalette();					updatePalette();					break;				case resetItem:					if (gCurrentColor == COLOR || gCurrentPat == -1)						defineColorPalette();					else						defineGrayPalette();					updatePalette();					break;			}			break;	}	HiliteMenu( 0 );}