 (-4091) {	"BogusWriters:"};/* Reserved for use by the Chooser. */resource 'STR ' (-4090) {	"BogusWriters:"};/* NBP timeout and retry information for AppleTalk devices. */resource 'GNRL' (-4096) {	11,		/* Retry interval	*/	5		/* Retry count		*/};/* Device PACKage resource [IM IV-217]. */// This is defined in new inside macintosh: devices page 1-42 onwards// in Zz's pascal sample the code resource was included in the // resource in the following way - as the THINK Rez compiler does// not understand the  $$resource directive we need to build the header// as described on pages 274 onwards in the THINK C 7 manual.////resource 'PACK' (-4096, purgeable) {//	128,		/* Device ID *///	10,			/* Version (1.0)	*///	usesAppleTalk,//	noPAP,//	noPostScript,//	noMultiples,//	noLeftButton,//	noRightButton,//	usesSavedZoneName,//	noActualZoneNames,//	acceptsInit,//	acceptsNewSel,//	acceptsFillList,//	acceptsGetSel,//	acceptsSelect,//	acceptsDeselect,//	ignoresTerminate,//	$$resource("PACKMan.CODE", 'CODE', 42)//};