or ( );	if ( theErr )	goto CleanupAndBail;		GetPort ( &savePort );	SetPortWindowPort ( theWindow );	BeginUpdate ( theWindow );					// visRgn temporarily = updateRgn	thePort = GetWindowPort ( theWindow );	UpdateDialog ( theWindow, thePort->visRgn );	DisposeRgn ( theRgn );		EndUpdate ( theWindow );					// restore normal visRgn of grafport	SetPort ( savePort );	CleanupAndBail:	return;}void DoContentClick ( WindowRef theWindow, EventRecord* theEvent ){	WindowRef	frontWindow;		// If a movable modal is active, ignore click in an inactive 	// window, otherwise select it or handle the content click.		frontWindow = FrontWindow ( );	if ( theWindow != frontWindow )	{		if ( IsMovableModal ( frontWindow ) )			SysBeep ( 30 );		else			SelectWindow ( theWindow );	}	else	{		SInt16		itemHit;				if ( DialogSelect ( theEvent, &theWindow, &itemHit ) && itemHit == 1 )		{			tThreadedOperationPtr theInfo;						theInfo = (tThreadedOperationPtr) GetWRefCon ( theWindow );			theInfo->bCancelled = true;		}	}		return;	} // DoContentClickvoid DoDragWindow ( WindowRef theWindow, EventRecord* theEvent ){	WindowRef	frontWindow;			// If a movable modal is active, ignore click in an inactive 	// title bar, otherwise let the Window Manager handle it.		frontWindow = FrontWindow ( );	if ( theWindow != frontWindow && IsMovableModal ( frontWindow ) )		SysBeep ( 30 );	else									{		RgnHandle	theRgn;		Rect		dragRect;				theRgn = GetGrayRgn ( );		dragRect = (*theRgn)->rgnBBox;		DragWindow ( theWindow, theEvent->where, &dragRect );	}		return;}