List;/*--------------------------	Routines--------------------------*/long EasyDialog(Rect *windowRect,		beDialogUpdate updateProc,		beDialogKey keyProc,		beDialogClick clickProc,		beDialogIdle idleProc,		EasyDialogButtonList *buttonList,		long refcon);short EasyDialogMessage(short iconID,		StringPtr header,StringPtr body,		EasyDialogButtonList *buttonList);Boolean EasyDialogGetString(const StringPtr dialogTitle,		const StringPtr dialogPrompt,		StringPtr inOutString,short maxInOutStringLength);Boolean EasyDialogGetNumber(const StringPtr dialogTitle,		const StringPtr dialogPrompt,		long *inOutNumber);Boolean EasyFieldDialog(short dialogID,EasyFieldList *fieldList,void *data,		StringPtr dialogTitle);