/*	File:		VU-Meter.r	Contains:	Defines needed to make a fat 'thng' resource.	Written by: Mark Cookson		Copyright:	Copyright © 1996-1999 by Apple Computer, Inc., All Rights Reserved.				You may incorporate this Apple sample source code into your program(s) without				restriction. This Apple sample source code has been provided "AS IS" and the				responsibility for its operation is yours. You are not permitted to redistribute				this Apple sample source code as "Apple sample source code" after having made				changes. If you're going to re-distribute the source, we require that you make				it clear in the source that the code was descended from Apple sample source				code, but that you've made changes.	Change History (most recent first):				8/20/1999	Karl Groethe	Updated for Metrowerks Codewarror Pro 2.1				*/#define UseExtendedThingResource	1				// we want the extended thng resource//#define FATCOMPONENT				1#include "Types.r"#include "SysTypes.r"#include "Components.r"#include "VU-Meter.h"#define kManufacturerType		'DTS '				// manufacturer#define VUMeterID				911					// resource ID for VU Meter//#define kSoundComponentType		'sift'				/*component type*/#define kSoundComponentPPCType	'nift'				/*component type for PowerPC code*/#define kSoundEffectsType		'snfx'#define kVUMeterFlags			(cmpWantsRegisterMessage)// VU Meter, PPCresource 'thng' (VUMeterID, "VU-Meter") {	kSoundEffectsType,														// type	kVUSelectorSubType,														// subtype	kManufacturerType,														// manufacture	kVUMeterFlags,															// flags	kAnyComponentFlagsMask,													// flags mask	0,																		// no 68K code	0,	'STR ',																	// name type	VUMeterID,																// name ID	'STR ',																	// info type	VUMeterID+1,															// info ID	'ICON',																	// icon type	VUMeterID,																// icon ID	kSoundComponentVersion,													// version	componentDoAutoVersion | componentHasMultiplePlatforms,					// registration flags	0,																		// icon family resource ID	{		kVUMeterFlags,														// flags		kSoundComponentPPCType,												// code type		VUMeterID,															// code ID		platformPowerPC														// platform	};};resource 'ICON' (VUMeterID, purgeable) {	$"0000 0000 0000 0000 0000 0000 0000 0000"	$"0000 0000 0000 0000 0000 0000 0044 8800"	$"0044 8800 0028 8800 0028 8800 0010 8800"	$"0010 7000 0000 0000 0000 0000 0000 0000"	$"0000 0000 0000 0000 22F7 DEF0 3681 1088"	$"2A81 1088 22E1 1CF0 2281 1090 2281 1088"	$"22F1 1E88"};resource 'STR ' (VUMeterID, purgeable) {	"VU-Meter"};resource 'STR ' (VUMeterID+1, purgeable) {	"A pre-mixer component for monitoring the sound output channel"};resource 'vers' (1) {	0x0,	0x50,	development,	0x1,	verUS,	"VU-Meter 0.5",	"VU-Meter 0.5\n© 1994-1999 Apple Computer, Inc."};