tworkState gNetState;extern Boolean gGotEndGameMessage;extern Boolean gNetSprocketPresent;extern Boolean gReceivedInput;extern Str31 kNetworkGameName;extern Str31 kJoinDialogLabel;extern Str31 kNBPType;		// This should be the same as kGameID//¥	------------------------------	Public Functions#ifdef __cplusplusextern "C" {#endifextern Boolean InitNetworking(void);extern Boolean NetSprocketPresent(void);extern Boolean DoHostGame(void);extern Boolean DoJoinGame(void);extern void ShutdownNetworking(void);extern void HandleNetworking(void);extern void SendInputState(Boolean left, Boolean right, Boolean fire);extern void SendEndGame();extern Boolean WaitForAllPlayers(void);extern void SendStartGame(void);#ifdef __cplusplus}#endif#endif