;	EndpointRef originalEndpoint;	OTClient originalOwner;	EndpointRef newEndpoint;	TEndpointInfo newEndpointInfo;	// Use the factory library to create an endpoint.		err = FactoryCreateEndpoint(&originalEndpoint, &originalOwner);		if (err == noErr) {			// Transfer the ownership of that endpoint, so that OT knows		// we now own it.				newEndpoint = OTTransferProviderOwnership(originalEndpoint, originalOwner, &err);		if (err == noErr) {						// We can now use newEndpoint as if we created it.  We call			// OTGetEndpointInfo as an example of an operation on the			// endpoint.			err = OTGetEndpointInfo(newEndpoint, &newEndpointInfo);						if (err == noErr) {				printf("Maximum size of endpoint address = %ld.\n", newEndpointInfo.addr);			}						OTCloseProvider(newEndpoint);		}	}		return err;}////////////////////////////////////////////////////////////void main(void)	// The main line.  This inits our connection to OpenTransport,	// and then inits the provider factory library.  It then	// calls GetProviderFromFactory to demo the use of	// OTTransferProviderOwnership.{	OSStatus err;		err = InitOpenTransport();	if (err == noErr) {			err = InitProviderFactory();				if (err == noErr) {						err = GetProviderFromFactory();			CloseProviderFactory();		}			CloseOpenTransport();	}	if (err == noErr) {		printf("Success!\n");	} else {		printf("Failed with error %ld.\n", err);	}}